sys_script.script:
(function executeRule(current, previous /*null on insert*/) {
  // Load student and job records
  var stu = current.student.getRefRecord ? current.student.getRefRecord() : null;
  var job = current.job.getRefRecord ? current.job.getRefRecord() : null;

  if (!stu || !job) {
    // If references not present, mark ineligible and continue
    current.eligibility_status = 'Ineligible';
    return;
  }

  // Get CGPA numbers (be defensive)
  var stuCgpa = parseFloat(stu.getValue('cgpa') || 0);
  var minCgpa = parseFloat(job.getValue('min_cgpa') || 0);

  // Branch check: job may have an 'eligible_branch' choice
  var jobBranch = (job.getValue('eligible_branch') || '').toString().trim();
  var stuBranch = (stu.getValue('branch') || '').toString().trim();

  var eligible = true;
  var reasons = [];

  if (stuCgpa < minCgpa) {
    eligible = false;
    reasons.push('CGPA below minimum');
  }
  if (jobBranch && jobBranch !== '' && jobBranch.toUpperCase() !== stuBranch.toUpperCase()) {
    eligible = false;
    reasons.push('Branch not eligible');
  }

  current.eligibility_status = eligible ? 'Eligible' : 'Ineligible';
  // date_applied: ensure it's set
  if (!current.date_applied)
    current.date_applied = new GlideDateTime();

})(current, previous);
